properties.query:
// Query: External Upstream Source Added to Azure DevOps Feed
// Connector: AzureDevOpsAuditing
// Author: Martin Kubinec
// Created: 24.07.2023
// Last Update: 24.07.2023 by MK
// Version 1.0
AzureDevOpsAuditing
// Filtruj zdroje pridane na Ãºrovni organizace nebo projektu
| where OperationName matches regex 'Artifacts.Feed.(Org|Project).Modify'
    and Details has 'UpstreamSources, added'
// Vytvor sloupce z Data
| extend FeedName = tostring(Data.FeedName),
         FeedId = tostring(Data.FeedId),
         UpstreamsAdded = Data.UpstreamsAdded
// Expand pro rozdeleni jednotlivych zdroju
| mv-expand UpstreamsAdded
// Vytvor sloupce z UpstreamsAdded
| extend SourceLocation = tostring(UpstreamsAdded.Location),
         SourceName = tostring(UpstreamsAdded.Name),
         SourceType = tostring(UpstreamsAdded.UpstreamSourceType),
         SourceProtocol = tostring(UpstreamsAdded.Protocol),
         SourceStatus = tostring(UpstreamsAdded.Status)
// Filtruj pouze externi zdroje
| where SourceType !~ 'internal'
// Zobraz serazene vysledky
| project-reorder TimeGenerated, OperationName, ScopeDisplayName, ProjectName, ProjectId, FeedName, FeedId, SourceName, SourceLocation, SourceProtocol, ActorDisplayName, ActorUPN, ActorUserId, UserAgent, IpAddress
| sort by TimeGenerated desc
